# import stopwords
stopwords = []
with open("stopwords.txt", 'r') as file:
    for line in file:
        stopwords.append(line.strip('\n'))


def tokenize(content):
    tokens = re.findall(r'[a-z]+', content.lower())
    return tokens


# O(n) where n is the size of TokenList since other operations like updating the dictionary or checking for existence are O(1)
def wordfreq(tokenlist, tokenmap):
    for v in tokenlist:
        if v not in tokenmap.keys():
            tokenmap[v] = 1
        else:
            tokenmap[v] = tokenmap[v] + 1

# O(n log n) since sorting in python has a complexity of O(n log n) worst case and printing is O(n)


def printfreq(frequencies):
    sorted_frequencies = sorted(
        frequencies.items(), key=lambda item: (-item[1], item[0].lower()))
    limit = 0
    for token, count in sorted_frequencies:
        if limit == 50:
            break
        elif token not in stopwords:
            limit += 1
            print(f"{limit}: {token} = {count}")
        else:
            continue


def save_to_json(frequencies, filename):
    data = {}
    if os.path.exists(filename):
        with open(filename, 'r') as file:
            data = json.load(file)

    for token, count in frequencies.items():
        if token not in stopwords:
            if token in data.keys():
                data[token] = data[token] + count
            elif not token in data.keys():
                data[token] = count

    with open(filename, 'w') as file:
        json.dump(data, file)


def save_links(links, filename):
    data = []
    if os.path.exists(filename):
        with open(filename, 'r') as file:
            data = json.load(file)
    for i in links:
        if i not in data:
            data.append(i)

    with open(filename, 'w') as file:
        json.dump(data, file)


